const html =  $input.first().json.data|| ""; // Asume que el HTML está en la propiedad "html"

const regex = /<a name="Iniciativa\d+"><\/a>/g;
const matches = [...html.matchAll(regex)];

const result = [];

for (let i = 0; i < matches.length; i++) {
  const startIndex = matches[i].index;
  const endIndex = (i + 1 < matches.length) ? matches[i + 1].index : html.length;

  const section = html.substring(startIndex, endIndex).trim();
  result.push({ json: { iniciativa_html: section } });
}

return result;

