const html = $input.first().json.data || "";

// Extraer el contenido del <body>
const bodyMatch = html.match(/<body[^>]*>([\s\S]*?)<\/body>/i);
let bodyContent = bodyMatch ? bodyMatch[1] : html;

// Reemplazos para conservar saltos de línea
bodyContent = bodyContent
  .replace(/<br\s*\/?>/gi, '\n')
  .replace(/<\/p>/gi, '\n\n')
  .replace(/<\/h[1-6]>/gi, '\n\n')
  .replace(/<\/li>/gi, '\n')
  .replace(/<\/div>/gi, '\n')
  .replace(/<li>/gi, '- ');

// Eliminar scripts y estilos
bodyContent = bodyContent
  .replace(/<script[\s\S]*?<\/script>/gi, '')
  .replace(/<style[\s\S]*?<\/style>/gi, '');

// Eliminar etiquetas HTML restantes
let textOnly = bodyContent
  .replace(/<\/?[^>]+(>|$)/g, '')
  .replace(/\n{3,}/g, '\n\n')
  .replace(/[ \t]+\n/g, '\n')
  .replace(/\s{2,}/g, ' ')
  .trim()
  .normalize("NFC"); // Asegura que los acentos estén bien codificados

// Crear el archivo binario con codificación UTF-8
const buffer = Buffer.from(textOnly, 'utf8');

return [
  {
    binary: {
      data: {
        data: buffer.toString('base64'), // base64 es requerido para archivos
        mimeType: 'text/plain',
        fileName: 'contenido_extraido.txt',
      },
    },
    json: {
      mensaje: "Archivo de texto generado correctamente",
    },
  },
];

