const inputText = $input.first().json.html; // Asegúrate de que tu entrada tenga una propiedad "texto"

function cleanTextForJSON(text) {
    if (typeof text !== 'string') return '';

    return text
        // Reemplaza rutas relativas de imágenes a absolutas
        .replace(/(src|SRC)=["']\/Gaceta\//g, 'src="https://gaceta.diputados.gob.mx/Gaceta/')
        // Escapa backslashes y comillas
        .replace(/\\/g, '\\\\')
        .replace(/"/g, '\\"')
        .replace(/\r?\n/g, '\\n')
        .replace(/[\u0000-\u001F]+/g, '');
}

return [
  {
    json: {
      texto_original: inputText,
      texto_limpio: cleanTextForJSON(inputText)
    }
  }
];

