// Paso 1: quitar etiquetas <pre> y </pre>
let raw = $input.first().json.results[0].cleaned_html;
raw = raw.replace(/^<pre>/, "").replace(/<\/pre>$/, "");

// Paso 2: convertir el string a JSON real (array)
const parsed = JSON.parse(raw);

// Paso 3: convertir cada objeto del array en un item separado
return parsed.map(obj => ({ json: obj }));

// Este código concatena todos los items con formato: "tema - sub_tema"
// separados por saltos de línea

const concatenado = items
  .map(item => `TEMA: ${item.json.tema} - SUBTEMA:${item.json.sub_tema}`)
  .join('\n');

return [
  {
    json: {
      prompt_llm: concatenado
    }
  }
];

return items.map(item => {
  if (item.json.temas_suscrit && Array.isArray(item.json.temas_suscrit)) {
    item.json.temas_suscrit_unido = item.json.temas_suscrit.join(', ');
  } else {
    item.json.temas_suscrit_unido = '';
  }
  return item;
});

return items.map(item => {
  let listas = [];
  if (typeof item.json.listas_distribucion === 'string') {
    try {
      listas = JSON.parse(item.json.listas_distribucion);
    } catch (e) {
      listas = [];
    }
  } else if (Array.isArray(item.json.listas_distribucion)) {
    listas = item.json.listas_distribucion;
  }
  // Extraer todos los correos y unirlos en un string
  item.json.correos = listas.flatMap(obj => obj.correos || []).join(', ');
  return item;
});

// Une todos los campos temas_suscrit_unido, separados por coma, y elimina repetidos

// 1. Une todo en un solo string
const unidos = items.map(item => item.json.temas_suscrit_unido).join(', ');

// 2. Separa por coma, elimina espacios extras y repetidos
const set = new Set(
  unidos
    .split(',')
    .map(x => x.trim())   // elimina espacios
    .filter(x => x)       // elimina vacíos
);

// 3. Une de nuevo a un solo string separado por coma
const resultado = Array.from(set).join(', ');

// 4. Regresa como un solo item
return [
  {
    json: {
      temas_suscrit_unido_unico: resultado
    }
  }
];

// Asume que el string está en el campo temas_suscrit_unido_unico del primer item

const temas = $input.first().json.message.content
  .split(',')
  .map(x => x.trim())
  .filter(x => x); // elimina vacíos

return temas.map(t => ({
  json: { tema: t }
}));

return items.map(item => {
  // Convierte el string de temas en array y limpia espacios
  const temas = (item.json.temas_suscrit_unido || '')
    .split(',')
    .map(x => x.trim());

  // El tema a comparar
  const tema = (item.json.tema || '').trim();

  // Busca coincidencia exacta
