// Procesa todos los items de entrada
return items.map(item => {
  const src = item.json;

  // Leer y parsear listas_distribucion
  let raw = src.listas_distribucion;
  if (!raw) {
    return { json: { ...src, correos_csv: "" } };
  }

  let lista;
  if (typeof raw === 'string') {
    try {
      lista = JSON.parse(raw);
    } catch (e) {
      return { json: { ...src, correos_csv: "" } };
    }
  } else {
    lista = raw;
  }

  if (!Array.isArray(lista) || lista.length === 0) {
    return { json: { ...src, correos_csv: "" } };
  }

  const { correos = [] } = lista[0];

  // Limpiar y unir correos en una cadena separada por comas
  const cleanCorreos = [...new Set(
    correos.map(c => String(c).trim()).filter(c => c)
  )];
  const correos_csv = cleanCorreos.join(',');

  // Devolver el item con campo nuevo
  return {
    json: {
      ...src,
      correos_csv
    }
  };
});

return items.map(item => {
  if (item.json.temas_suscrit && Array.isArray(item.json.temas_suscrit)) {
    item.json.temas_suscrit_unido = item.json.temas_suscrit.join(', ');
  } else {
    item.json.temas_suscrit_unido = '';
  }
  return item;
});

// Entrada: varios items con { id_senado_doc, temas: "A, B, C" }
// Salida: un item por cada tema, repitiendo id_senado_doc

const results = [];

for (const item of items) {
  const id = item.json.id_senado_doc;
   const fuente = item.json.fuente;
   const titulo = item.json.titulo;
  // leer el campo correcto
  let raw = (item.json.temas ?? "").toString();

  // normalizar espacios
  raw = raw.replace(/\s+/g, " ").trim();

  // separar por coma (acepta variantes y repeticiones)
  const parts = raw
    .split(/[,;，、]+/)          // coma/semicolon/variantes
    .map(s => s.trim())
    .filter(Boolean);           // quita vacíos

  for (const tema of parts) {
    results.push({
      json: {
        id_senado_doc: id,
        fuente: fuente,
        titulo: titulo,
        tema
      }
    });
  }
}

// si no salió nada, revisa que "temas" exista en los items de entrada
return results;

// Entrada: varios items con { id_senado_doc, temas: "A, B, C" }
// Salida: un item por cada tema, repitiendo id_senado_doc

const results = [];

for (const item of items) {
  const id = item.json.id_alerta;
  // leer el campo correcto
  let raw = (item.json.temas_alerta ?? "").toString();

  // normalizar espacios
  raw = raw.replace(/\s+/g, " ").trim();

  // separar por coma (acepta variantes y repeticiones)
  const parts = raw
    .split(/[,\-\/]+/)              // coma/semicolon/variantes
    .map(s => s.trim())
    .filter(Boolean);           // quita vacíos

  for (const tema_alerta of parts) {
    results.push({
      json: {
        id_alerta: id,
        tema_alerta
      }
    });
  }
}

// si no salió nada, revisa que "temas" exista en los items de entrada
return results;
